package 비교문;

public class IfTest1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a = 4;
       if(a < 0) {
    	   //a = 4이므로 0보다 큰 양수 입니다.
    	   System.out.println("a = "+a+"이므로 0보다 작은 음수 입니다.");
       }else {
    	   System.out.println("a = "+a+"이므로 0보다 큰 양수 입니다.");
       }
	}// main() method end

}//IfTest1 end
